// src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import { login as loginApi, getUsers as fetchUsersApi } from '../api/api'; // Import your API functions

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null); // Stores user info
    const [token, setToken] = useState(localStorage.getItem('jwtToken')); // Get token from localStorage
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const [users, setUsers] = useState([]); // For storing user list

    // Effect to load user from localStorage on mount if token exists
    useEffect(() => {
        const storedToken = localStorage.getItem('jwtToken');
        if (storedToken) {
            setToken(storedToken);
            setUser({ id: 'admin_user_001', role: 'admin', name: 'Admin User' });
        } else {
            setUser(null);
        }
    }, []);

    const login = async (email, password) => {
        setLoading(true);
        setError(null);
        try {
            const userData = await loginApi(email, password);
            setToken(userData.token);
            setUser(userData.user); // Assuming your login API returns { token, user: { id, email, role, name } }
            localStorage.setItem('jwtToken', userData.token);
            return true; // Indicate successful login
        } catch (err) {
            setError(err.message || 'Login failed');
            setToken(null);
            setUser(null);
            localStorage.removeItem('jwtToken');
            return false; // Indicate failed login
        } finally {
            setLoading(false);
        }
    };

    const logout = () => {
        setToken(null);
        setUser(null);
        localStorage.removeItem('jwtToken');
    };

    // Function to fetch users and set them in context
    const fetchUsers = async () => {
        setLoading(true); // You might have a separate loading state for users
        setError(null);
        try {
            const userList = await fetchUsersApi();
            setUsers(userList);
        } catch (err) {
            setError(err.message || 'Failed to fetch users');
            setUsers([]); // Clear users on error
        } finally {
            setLoading(false);
        }
    };

    // Add functions for addUser, deleteUser, updateUser here as well
    // ...

    return (
        <AuthContext.Provider value={{ token, user, login, logout, error, loading, users, fetchUsers }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
