// src/components/PrivateRoute.js
import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // Make sure this path is correct

const PrivateRoute = ({ children }) => {
    // Get token, user, and loading state from AuthContext
    const { token, user, loading } = useAuth(); // Added user to check

    if (loading) {
        // Display a loading indicator while checking auth status
        return <div style={{ textAlign: 'center', paddingTop: '50px' }}>Loading authentication status...</div>;
    }

    // If logged in (token or user exists), render the child components
    // If not logged in, navigate to the login page
    // return token ? children : <Navigate to="/login" replace />;
    return user ? children : <Navigate to="/login" replace />; // Prefer checking 'user' as it's set after login
};

export default PrivateRoute;
