// src/components/PrivateRoute.js
import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const PrivateRoute = ({ children }) => {
    const { token, user, loading } = useAuth(); // Make sure user is also fetched/available

    if (loading) {
        // Display a loading indicator while checking auth status
        return <div style={{ textAlign: 'center', paddingTop: '50px' }}>Loading...</div>;
    }

    // If logged in (user exists), render the protected content
    // If not logged in, navigate to the login page
    return user ? children : <Navigate to="/login" replace />;
};

export default PrivateRoute;
